// AUSound class for AsterRaider// by Mike Leeimport java.applet.Applet;import java.applet.AudioClip;import java.io.File;import java.net.URL;import java.net.MalformedURLException;public class AUSound {	// Fields	AudioClip sound;	// Constructor	public AUSound(String filename) {		try {			File file = new File(filename);			sound = Applet.newAudioClip(file.toURL());		} catch(MalformedURLException murle) {			System.out.println("Error loading file: "+filename);		}	}	public AUSound(URL filepath) {		sound = Applet.newAudioClip(filepath);	}	public void play() {		sound.play();	}	public void loop() {		sound.loop();	}	public void stop() {		sound.stop();	}}