// ShipType class for AsterRaider// by Mike Leeimport java.awt.Color;import java.io.*;public class ShipType {	// Fields	public String typeName;	public SpaceSprite shipSprite;	public SpaceSprite shotSprite;	public AUSound shotSound;	public AUSound shieldSound;	public AUSound engineSound;	public double defaultAccel;	public int defaultTurnDelay;	public int defaultShotPower;	public Color shPartColor;	public double exSpeedFrac;	public int makeExDelay;	public int exPartLife;	public int exPartSize;	public Color exPartColor;	public int exhaustPorts;	public int exPortOffset; // only necessary for multiple exhaust ports	public int shieldRadius;	public Color shieldColorA;	public Color shieldColorB;	// Constructor	public ShipType(String name, SpaceSprite ss, SpaceSprite shot, AUSound sSound, AUSound enSound, AUSound shSound, double da, int dtd, int dsp, Color spc, double esf, int med, int epl, int eps, Color epc, int ep, int epo, int shr, Color sca, Color scb) {		typeName = name;		shipSprite = ss;		shotSprite = shot;		shotSound = sSound;		shieldSound = shSound;		engineSound = enSound;		defaultAccel = da;		defaultTurnDelay = dtd;		defaultShotPower = dsp;		shPartColor = spc;		exSpeedFrac = esf;		makeExDelay = med;		exPartLife = epl;		exPartSize = eps;		exPartColor = epc;		exhaustPorts = ep;		exPortOffset = epo;		shieldRadius = shr;		shieldColorA = sca;		shieldColorB = scb;	}	// Methods	public static ShipType[] getShipTypes(File shipFile, SpaceSprite[] sprites, AUSound[] sounds, AUSound[] loopSounds) {		try {			ShipType[] types;			FileReader fr = new FileReader(shipFile);			BufferedReader br = new BufferedReader(fr);			// Declare temporary variables			String name;			SpaceSprite ss, shot;			AUSound sSound, enSound, shSound;			double da;			int dtd, dsp;			double esf;			int med, epl, eps;			Color spc, epc;			int ep, epo, shr;			Color sca, scb;			int id,r,g,b;			// Read number of types			String line = br.readLine();			int numTypes = Integer.parseInt(br.readLine());			types = new ShipType[numTypes];			for(int i=0; i<numTypes; i++) {				line = br.readLine();				// Read name				line = br.readLine();				name = br.readLine();				// Read ship sprite				line = br.readLine();				id = Integer.parseInt(br.readLine());				ss = sprites[id];				// Read shot sprite				line = br.readLine();				id = Integer.parseInt(br.readLine());				shot = sprites[id];				// Read shot sound				line = br.readLine();				id = Integer.parseInt(br.readLine());				sSound = sounds[id];				// Read shield sound				line = br.readLine();				id = Integer.parseInt(br.readLine());				shSound = loopSounds[id];				// Read engine sound				line = br.readLine();				id = Integer.parseInt(br.readLine());				enSound = loopSounds[id];				// Read acceleration and turn delay				line = br.readLine();				da = (Double.valueOf(br.readLine())).doubleValue();				line = br.readLine();				dtd = Integer.parseInt(br.readLine());				// Read shot power and color				line = br.readLine();				dsp = Integer.parseInt(br.readLine());				line = br.readLine();				r = Integer.parseInt(br.readLine());				g = Integer.parseInt(br.readLine());				b = Integer.parseInt(br.readLine());				spc = new Color(r,g,b);				// Read exhaust parameters				line = br.readLine();				esf = 1/(double)(Integer.parseInt(br.readLine()));				line = br.readLine();				med = Integer.parseInt(br.readLine());				line = br.readLine();				epl = Integer.parseInt(br.readLine());				line = br.readLine();				eps = Integer.parseInt(br.readLine());				line = br.readLine();				r = Integer.parseInt(br.readLine());				g = Integer.parseInt(br.readLine());				b = Integer.parseInt(br.readLine());				epc = new Color(r,g,b);				line = br.readLine();				ep = Integer.parseInt(br.readLine());				line = br.readLine();				epo = Integer.parseInt(br.readLine());				// Read shield parameters				line = br.readLine();				shr = Integer.parseInt(br.readLine());				line = br.readLine();				r = Integer.parseInt(br.readLine());				g = Integer.parseInt(br.readLine());				b = Integer.parseInt(br.readLine());				sca = new Color(r,g,b);				line = br.readLine();				r = Integer.parseInt(br.readLine());				g = Integer.parseInt(br.readLine());				b = Integer.parseInt(br.readLine());				scb = new Color(r,g,b);				// Create ship type				types[i] = new ShipType(name,ss,shot,sSound,enSound,shSound,da,dtd,dsp,spc,esf,med,epl,eps,epc,ep,epo,shr,sca,scb);			}			br.close();			fr.close();			return types;		} catch(IOException ioe) {			AsterRaider.errManager.catchException(ioe,"Can't load ship types",true);		}		return null;	}}